#include "stdlib/stats/base/dists/t/cdf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/betainc.h"
#include "stdlib/math/base/special/beta.h"
#include "stdlib/math/base/special/sqrt.h"
#include "stdlib/constants/float64/pi.h"

/**
* Evaluates the cumulative distribution function (CDF) for a Student's t-distribution with `v` degrees of freedom at a value `x`.
*
* @param x    input value
* @param v    degrees of freedom
* @return     evaluated CDF
*
* @example
* double y = stdlib_base_dists_t_cdf( 1.0, 10.0 );
* // returns ~0.816
*/
double stdlib_base_dists_t_cdf( const double x, const double v ) {
    if ( stdlib_base_is_nan( x ) || stdlib_base_is_nan( v ) || v <= 0.0 ) {
        return 0.0 / 0.0;
    }

    double t2 = v / ( v + x * x );
    double bt = stdlib_base_betainc( t2, v / 2.0, 0.5, 1, 0 );
    double beta_norm = stdlib_base_beta( v / 2.0, 0.5 );

    if ( x >= 0.0 ) {
        return 1.0 - 0.5 * (bt / beta_norm);
    }
    return 0.5 * (bt / beta_norm);
}